CREATE DATABASE `distrodb` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;

USE `distrodb`;

CREATE TABLE `user` (
  `username` varchar(255) PRIMARY KEY,
  `password` varchar(255),
  `salt` varchar(255),
  `role` varchar(255),
  `img` varchar(255)
);

CREATE TABLE `distro` (
  `name` varchar(255) PRIMARY KEY,
  `based` varchar(255),
  `region` varchar(255),
  `arch` varchar(255),
  `desc` text,
  `web` text,
  `version` varchar(255)
);

CREATE TABLE `favs` (
  `username`  varchar(255),
  `distro`  varchar(255),
  PRIMARY KEY (`username`, `distro`)
);

CREATE TABLE `comments` (
  `id` int PRIMARY KEY,
  `username` varchar(255),
  `distro` varchar(255),
  `comment` varchar(2000),
  `date` varchar(255)
);

ALTER TABLE `favs` ADD FOREIGN KEY (`username`) REFERENCES `user` (`username`);

ALTER TABLE `favs` ADD FOREIGN KEY (`distro`) REFERENCES `distro` (`name`);

ALTER TABLE `comments` ADD FOREIGN KEY (`username`) REFERENCES `user` (`username`);

ALTER TABLE `comments` ADD FOREIGN KEY (`distro`) REFERENCES `distro` (`name`);

ALTER TABLE `comments` MODIFY `id` int NOT NULL AUTO_INCREMENT;

